    def to_json(self) -> t.Mapping[str, t.Any]:
        return {
            "presentationNodes": to_json(self.presentation_nodes),
            "collectibles": to_json(self.collectibles),
            "records": to_json(self.records),
            "metrics": to_json(self.metrics),
        }


