class OptInFlags(Enum):
    NONE = 0
    NEWSLETTER = 1
    SYSTEM = 2
    MARKETING = 4
    USER_RESEARCH = 8
    CUSTOMER_SERVICE = 16
    SOCIAL = 32
    PLAY_TESTS = 64
    PLAY_TESTS_LOCAL = 128
    CAREERS = 256


